    private void btnRegistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegistrarActionPerformed
        int dni = Integer.parseInt(txtDNI.getText());
        String nombre = txtNombre.getText();
        String apPat = txtApPat.getText();
        String apMat = txtApMat.getText();
        int celular = Integer.parseInt(txtCelular.getText());
        String correo = txtCorreo.getText();
        String tipo = txtTipo.getText();
        
        try{
            Connection con = Conexion.getConnection();
            PreparedStatement ps = con.prepareStatement("insert into TPersona (DNIPe, PNom, PApPat, PApMat, PCelular,PCorreo, IDTipo) values (?,?,?,?,?,?,?)");
            ps.setInt(1, dni);
            ps.setString(2, nombre);
            ps.setString(3, apPat);
            ps.setString(4, apMat);
            ps.setInt(5, celular);
            ps.setString(6, correo);
            ps.setString(7, tipo);
            
            ps.executeUpdate();//para ejecutar la consulta
            
            JOptionPane.showMessageDialog(null,"Registro guardado");
            
            txtDNI.setText("");
            txtNombre.setText("");
            txtApPat.setText("");
            txtApMat.setText("");
            txtCelular.setText("");
            txtCorreo.setText("");
            txtTipo.setText("");
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null,e.toString());
        }
    }//GEN-LAST:event_btnRegistrarActionPerformed

